mapper.insert:
/* save */
INSERT INTO tb_comment (

) VALUES (
    #{id}
    , #{itemId}
    , #{content}
    , #{writer}
    , 0
    , NOW()
    , NULL
)

mapper.select:
/* findById */
SELECT

FROM
    tb_comment
WHERE
    id = #{id}

mapper.update:
/* update */
UPDATE tb_comment
SET
    modified_date = NOW()
    , content = #{content}
    , writer = #{writer}
WHERE
    id = #{id}

mapper.delete:
/* deleteById */
UPDATE tb_comment
SET
    delete_yn = 1
WHERE
    id = #{id}
