mapper.select:
/* findAll */ SELECT a.id , a.itemName , a.content , a.create_date , a.update_date , a.price , a.quantity , a.open , a.user_id , a.create_user , b.id AS file_id FROM tb_item a LEFT JOIN tb_file b ON a.id = b.item_id
and itemName like concat('%', #{itemName}, '%')
and price <= #{price}
GROUP BY a.id ORDER BY a.id desc LIMIT #{pagination.limitStart}, #{recordSize}

/* count */ SELECT COUNT(*) FROM tb_item
and itemName like concat('%', #{itemName}, '%')
and price <= #{price}

mapper.insert:
INSERT INTO tb_item (
    itemName
    , content
    , price
    , quantity
    , open
    , user_id
    , create_user
) VALUE (
    #{itemName}
    , #{content}
    , #{price}
    , #{quantity}
    , #{open}
    , #{userId}
    , #{createUser}
)


    SELECT LAST_INSERT_ID()

mapper.delete:
DELETE FROM tb_item
    WHERE 1=1
AND id = #{id}

mapper.update:
UPDATE tb_item SET
    itemName = #{itemName}
    , content = #{content}
    , price = #{price}
    , quantity = #{quantity}
    , open = #{open}
    , update_date = CURRENT_TIMESTAMP
    , update_user = #{updateUser}
WHERE
    id = #{id}


    SELECT
        a.id
    FROM
        tb_item a
    WHERE 1=1
        AND a.id = #{id}
