mapper.insert:
/* save */
INSERT INTO tb_member (

) VALUES (
    #{id}
    , #{loginId}
    , #{password}
    , #{nickname}
    , #{gender}
    , 0
    , NOW()
    , NULL
)

mapper.update:
/* update */
UPDATE tb_member
SET
    modified_date = NOW()
    , nickname = #{nickname}
    , gender = #{gender}

        , password = #{password}

WHERE
    id = #{id}

mapper.delete:
/* delete */
UPDATE tb_member
SET
    delete_yn = 1
WHERE
    id = #{id}
